pairs.map:
function(doc) {
	if((doc.doc_type == 'pairing' || doc.doc_type == 'intentional') && doc.data.length > 0){
  		emit(doc._id, doc);
	}
}

paircounts.map:
function(doc) {
	if(doc.doc_type == 'pairing' || doc.doc_type == 'intentional'   ) {
  		var names = doc.data[0] + ' :: ' + doc.data[1];
		emit(names,{"type": doc.doc_type, "ts":doc.timestamp});
	}
}

paircounts.reduce:
function(keys, values, rereduce) {
	var hist = {};
	 values.map( function (a) { 
		if (a.type in hist){
			var item = hist[a.type];
			item.count++ ;
			if(a.ts > item.last_ts) item.last_ts = a.ts
		}
		else hist[a.type] = {"count":1, "last_ts": a.ts}; } 

		);
	return hist;
}

oddcounts.reduce:
function(keys, values, rereduce){
	var ts = values.reduce(function(p,c,i) {
		return c > p ? c: p;
	});
	return {"odd":{"count": values.length,"last_ts": ts}};
}
